CREATE TABLE `chat` ( `sys_id` INT(11) NOT NULL AUTO_INCREMENT , `id` VARBINARY(16) NOT NULL , `author_id` VARBINARY(16) NOT NULL , `pseudo` VARCHAR(80) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `message` VARCHAR(1000) NOT NULL , `namespace` VARCHAR(80) NOT NULL , `room` VARCHAR(80) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , PRIMARY KEY (`sys_id`), UNIQUE (`id`)) ENGINE = InnoDB;