      SELECT MAX(permission) FROM permission WHERE 
        (entity_id IN ('nobody', 'ffffffffffffffff', '*') AND 
        (resource_id='*' OR resource_id=_rid) ) LIMIT 1 INTO _perm;
    END IF;
    
  END IF;
  SELECT IFNULL(_perm, 0) INTO _perm;
  RETURN _perm;
