        c.entity LIKE CONCAT(TRIM(_key), '%') ) AND c.status <> 'received'
    ORDER BY is_mycontact desc ,fullname ASC , c.id ASC  LIMIT _offset, _range;



  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
  
