CREATE DEFINER=`gopinath`@`%` PROCEDURE `count_yet_read_next`(
  IN _uid VARCHAR(16),
  IN _entity_id VARCHAR(16)
)
BEGIN
   
DECLARE _entity_id VARCHAR(16);
DECLARE _uid VARCHAR(16);
DECLARE _total_cnt int(11) unsigned;
DECLARE _room_cnt int(11) unsigned;
