  IN _chat_id varchar(16),
  IN _page INT(6)
)
BEGIN

  DECLARE _drumate_id VARCHAR(16);
  DECLARE _range bigint;
  DECLARE _offset bigint;
  DECLARE _sys_id bigint; 
  DECLARE _category varchar(50);  
 
	DROP TABLE IF EXISTS  _chat;
	CREATE TEMPORARY TABLE _chat (
