   SET @sql = CONCAT( 
    "SELECT CONCAT(" ,_des_db ,".parent_path(id),user_filename, '/') FROM " ,_des_db ,".media WHERE id =" ,QUOTE( _pid),
    "INTO @descpattern ");
  PREPARE stmt FROM @sql;
