        WHERE (ons + 10*tns +100*hrds +1)<=@lmt
        ) cnt 
    INNER JOIN (SELECT @lst as id) mytbl ON CHAR_LENGTH(mytbl.id)-CHAR_LENGTH(REPLACE(mytbl.id, @dlm, ''))>=cnt.n-1;
  


