  SELECT id, 
   JSON_UNQUOTE(JSON_EXTRACT(address, '$.street')) AS street,
   JSON_UNQUOTE(JSON_EXTRACT(address, '$.city')) AS city,
   JSON_UNQUOTE(JSON_EXTRACT(address, '$.country')) AS country
