  IN _rid VARCHAR(16),
  IN _eid VARCHAR(16)
)
BEGIN
  IF _eid != '' THEN
    DELETE FROM permission WHERE resource_id=_rid AND entity_id=_eid;
    IF _eid = 'nobody' or _eid ='*' THEN
        
         DELETE FROM permission WHERE resource_id=_rid and assign_via= 'link';
